import"./modulepreload-polyfill-3cfb730f.js";window.onload=async()=>{try{const t=await WA.player.state.checklist;f(t)}catch(t){console.error("Error fetching todos from WA API:",t)}};function f(t){document.querySelectorAll('input[name="filter"]').forEach(n=>{n.addEventListener("change",()=>l(t,n.value))}),l(t,"notDone")}function l(t,c){const n=document.querySelector(".all-count");n.textContent=t.length;const d=t.filter(e=>e.done).length,a=document.querySelector(".done-count");a.textContent=d;const i=t.filter(e=>!e.done).length,s=document.querySelector(".not-done-count");s.textContent=i;const u=c==="done"?t.filter(e=>e.done):c==="notDone"?t.filter(e=>!e.done):t,r=document.querySelector(".todo-list");r.innerHTML="",u.forEach(e=>{const o=document.createElement("li");o.classList.add("todo-item"),e.done&&o.classList.add("done"),o.textContent=e.title,r.appendChild(o)})}
